import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import DashboardPage from './pages/DashboardPage';
import OperationalTimePage from './pages/OperationalTimePage';
import './App.css';

function App() {
  const token = localStorage.getItem('token');
  return (
    <Router>
      <div style={{ background: '#222', color: '#fff', padding: '12px 24px', marginBottom: 24, display: 'flex', alignItems: 'center', justifyContent: 'space-between', flexWrap: 'wrap' }}>
        <div style={{ fontWeight: 'bold', fontSize: 22 }}>Machine Status</div>
        <nav style={{ display: 'flex', gap: 16, overflowX: 'auto', whiteSpace: 'nowrap' }}>
          <Link to="/dashboard" style={{ color: '#fff', marginRight: 20, textDecoration: 'none', fontWeight: 500, minWidth: 80, textAlign: 'center' }}>Dashboard</Link>
          <Link to="/operational-time" style={{ color: '#fff', textDecoration: 'none', fontWeight: 500, minWidth: 120, textAlign: 'center' }}>Operational Time</Link>
        </nav>
      </div>
      <Routes>
        <Route path="/login" element={<LoginPage />} />
        <Route path="/dashboard" element={token ? <DashboardPage /> : <Navigate to="/login" />} />
        <Route path="/operational-time" element={<OperationalTimePage />} />
        <Route path="*" element={<Navigate to={token ? "/dashboard" : "/login"} />} />
      </Routes>
    </Router>
  );
}
export default App;
