import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, Link } from 'react-router-dom';
import LoginPage from './pages/LoginPage';
import DashboardPage from './pages/DashboardPage';
import OperationalTimePage from './pages/OperationalTimePage';
import './App.css';

function App() {
  const token = localStorage.getItem('token');
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<LoginPage />} />
        <Route path="/dashboard" element={token ? <><div className="app-header"><div className="app-title">Machine Status</div><nav className="app-nav"><Link to="/dashboard" className="app-link">Dashboard</Link><Link to="/operational-time" className="app-link">Operational Time</Link></nav></div><DashboardPage /></> : <Navigate to="/login" />} />
        <Route path="/operational-time" element={token ? <><div className="app-header"><div className="app-title">Machine Status</div><nav className="app-nav"><Link to="/dashboard" className="app-link">Dashboard</Link><Link to="/operational-time" className="app-link">Operational Time</Link></nav></div><OperationalTimePage /></> : <Navigate to="/login" />} />
        <Route path="*" element={<Navigate to={token ? "/dashboard" : "/login"} />} />
      </Routes>
    </Router>
  );
}
export default App;
